  $(document).ready(function(){

    $("#jquery_jplayer_1").jPlayer({
      ready: function () {
        $(this).jPlayer("setMedia", {
          m4a: "http://play.kickthebox.com:8000/listen"
        }).jPlayer("play");
      },
      ended: function (event) {
        $(this).jPlayer("play");
      },
      supplied: "m4a"
    });
  });
